   146	public class FindNullDeref implements Detector, UseAnnotationDatabase, NullDerefAndRedundantComparisonCollector {
   147	    private final Logger log = LoggerFactory.getLogger(getClass());
   148	
   149	    public static final boolean DEBUG = SystemProperties.getBoolean("fnd.debug");
   150	
   151	    private static final boolean DEBUG_NULLARG = SystemProperties.getBoolean("fnd.debug.nullarg");
   152	
   153	    private static final boolean DEBUG_NULLRETURN = SystemProperties.getBoolean("fnd.debug.nullreturn");
   154	
   155	    private static final boolean MARK_DOOMED = SystemProperties.getBoolean("fnd.markdoomed", true);
   156	
   157	    private static final String METHOD_NAME = SystemProperties.getProperty("fnd.method");
   158	
   159	    private static final String CLASS = SystemProperties.getProperty("fnd.class");
   160	
   161	    // Fields
   162	    private final BugReporter bugReporter;
   163	
   164	    private final BugAccumulator bugAccumulator;
   165	
   166	    // Cached database stuff
   167	    private ParameterNullnessPropertyDatabase unconditionalDerefParamDatabase;
   168	
   169	    private boolean checkedDatabases = false;
   170	
   171	    // Transient state
   172	    private ClassContext classContext;
   173	
   174	    private Method method;
   175	
   176	    private IsNullValueDataflow invDataflow;
   177	
   178	    private ValueNumberDataflow vnaDataflow;
   179	
   180	    private BitSet previouslyDeadBlocks;
   181	
   182	    private NullnessAnnotation methodAnnotation;
   183	
   184	    public FindNullDeref(BugReporter bugReporter) {
   185	        this.bugReporter = bugReporter;
   186	        this.bugAccumulator = new BugAccumulator(bugReporter);
   187	    }
   188	
   189	    @Override
   190	    public void visitClassContext(ClassContext classContext) {
   191	        this.classContext = classContext;
   192	
   193	        String currentMethod = null;
   194	
   195	        JavaClass jclass = classContext.getJavaClass();
   196	        String className = jclass.getClassName();
   197	        if (CLASS != null && !className.equals(CLASS)) {
   198	            return;
   199	        }
   200	
   201	        List<Method> methodsInCallOrder = classContext.getMethodsInCallOrder();
   202	        for (Method method : methodsInCallOrder) {
   203	            try {
   204	                if (method.isAbstract() || method.isNative() || method.getCode() == null) {
   205	                    continue;
   206	                }
   207	
   208	                currentMethod = SignatureConverter.convertMethodSignature(jclass, method);
   209	
   210	                if (METHOD_NAME != null && !method.getName().equals(METHOD_NAME)) {
   211	                    continue;
   212	                }
   213	                if (DEBUG || DEBUG_NULLARG) {
   214	                    System.out.println("Checking for NP in " + currentMethod);
   215	                }
   216	                analyzeMethod(classContext, method);
   217	            } catch (MissingClassException e) {
   218	                bugReporter.reportMissingClass(e.getClassNotFoundException());
   219	            } catch (DataflowAnalysisException e) {
   220	                bugReporter.logError("While analyzing " + currentMethod + ": FindNullDeref caught dae exception", e);
   221	            } catch (CFGBuilderException e) {
   222	                bugReporter.logError("While analyzing " + currentMethod + ": FindNullDeref caught cfgb exception", e);
   223	            }
   224	            bugAccumulator.reportAccumulatedBugs();
   225	        }
   226	    }
   227	
   228	    private void analyzeMethod(ClassContext classContext, Method method) throws DataflowAnalysisException, CFGBuilderException {
   229	        if (DEBUG || DEBUG_NULLARG) {
   230	            System.out.println("Pre FND ");
   231	        }
   232	
   233	        if ((method.getAccessFlags() & Const.ACC_BRIDGE) != 0) {
   234	            return;
   235	        }
   236	
   237	        MethodGen methodGen = classContext.getMethodGen(method);
   238	
   239	        if (methodGen == null) {
   240	            return;
   241	        }
   242	        if (!checkedDatabases) {
   243	            checkDatabases();
   244	            checkedDatabases = true;
   245	        }
   246	
   247	        XMethod xMethod = XFactory.createXMethod(classContext.getJavaClass(), method);
   248	
   249	        // For junit 4 only (does not apply to junit 5)
   250	        ClassDescriptor junitTestAnnotation = DescriptorFactory.createClassDescriptor("org/junit/Test");
   251	        AnnotationValue av = xMethod.getAnnotation(junitTestAnnotation);
   252	        if (av != null) {
   253	            Object value = av.getValue("expected");
   254	
   255	            if (value instanceof Type) {
   256	                String className = ((Type) value).getClassName();
   257	                if ("java.lang.NullPointerException".equals(className)) {
   258	                    return;
   259	                }
   260	            }
   261	        }
   262	
   263	        // UsagesRequiringNonNullValues uses =
   264	        // classContext.getUsagesRequiringNonNullValues(method);
   265	        this.method = method;
   266	        this.methodAnnotation = getMethodNullnessAnnotation();
   267	
   268	        if (DEBUG || DEBUG_NULLARG) {
   269	            System.out.println("FND: " + SignatureConverter.convertMethodSignature(methodGen));
   270	        }
   271	
   272	        this.previouslyDeadBlocks = findPreviouslyDeadBlocks();
   273	
   274	        // Get the IsNullValueDataflow for the method from the ClassContext
   275	        invDataflow = classContext.getIsNullValueDataflow(method);
   276	
   277	        vnaDataflow = classContext.getValueNumberDataflow(method);
   278	
   279	        // Create a NullDerefAndRedundantComparisonFinder object to do the
   280	        // actual
   281	        // work. It will call back to report null derefs and redundant null
   282	        // comparisons
   283	        // through the NullDerefAndRedundantComparisonCollector interface we
   284	        // implement.
   285	        NullDerefAndRedundantComparisonFinder worker = new NullDerefAndRedundantComparisonFinder(classContext, method, this);
   286	        worker.execute();
   287	
   288	        checkCallSitesAndReturnInstructions();
   289	
   290	    }
   291	
   292	    /**
   293	     * Find set of blocks which were known to be dead before doing the null
   294	     * pointer analysis.
   295	     *
   296	     * @return set of previously dead blocks, indexed by block id
   297	     * @throws CFGBuilderException
   298	     * @throws DataflowAnalysisException
   299	     */
   300	    private BitSet findPreviouslyDeadBlocks() throws DataflowAnalysisException, CFGBuilderException {
   301	        BitSet deadBlocks = new BitSet();
   302	        ValueNumberDataflow vnaDataflow = classContext.getValueNumberDataflow(method);
   303	        for (Iterator<BasicBlock> i = vnaDataflow.getCFG().blockIterator(); i.hasNext();) {
   304	            BasicBlock block = i.next();
   305	            ValueNumberFrame vnaFrame = vnaDataflow.getStartFact(block);
   306	            if (vnaFrame.isTop()) {
   307	                deadBlocks.set(block.getLabel());
   308	            }
   309	        }
   310	
   311	        return deadBlocks;
   312	    }
   313	
