   333	    private NullnessAnnotation getMethodNullnessAnnotation() {
   334	
   335	        if (method.getSignature().indexOf(")L") >= 0 || method.getSignature().indexOf(")[") >= 0) {
   336	            if (DEBUG_NULLRETURN) {
   337	                System.out.println("Checking return annotation for "
   338	                        + SignatureConverter.convertMethodSignature(classContext.getJavaClass(), method));
   339	            }
   340	
   341	            XMethod m = XFactory.createXMethod(classContext.getJavaClass(), method);
   342	            return AnalysisContext.currentAnalysisContext().getNullnessAnnotationDatabase().getResolvedAnnotation(m, false);
   343	        }
   344	        return NullnessAnnotation.UNKNOWN_NULLNESS;
   345	    }
   346	
   347	    static class CheckCallSitesAndReturnInstructions {
   348	    }
   349	
   350	    private void checkCallSitesAndReturnInstructions() {
   351	        Profiler profiler = Global.getAnalysisCache().getProfiler();
   352	        profiler.start(CheckCallSitesAndReturnInstructions.class);
   353	        try {
   354	            ConstantPoolGen cpg = classContext.getConstantPoolGen();
   355	            TypeDataflow typeDataflow = classContext.getTypeDataflow(method);
   356	
   357	            for (Iterator<Location> i = classContext.getCFG(method).locationIterator(); i.hasNext();) {
   358	                Location location = i.next();
   359	                Instruction ins = location.getHandle().getInstruction();
   360	                try {
   361	                    ValueNumberFrame vnaFrame = classContext.getValueNumberDataflow(method).getFactAtLocation(location);
   362	                    if (!vnaFrame.isValid()) {
   363	                        continue;
   364	                    }
   365	
   366	                    if (ins instanceof InvokeInstruction) {
   367	                        examineCallSite(location, cpg, typeDataflow);
   368	                    } else if (methodAnnotation == NullnessAnnotation.NONNULL && ins.getOpcode() == Const.ARETURN) {
   369	                        examineReturnInstruction(location);
   370	                    } else if (ins instanceof PUTFIELD) {
   371	                        examinePutfieldInstruction(location, (PUTFIELD) ins, cpg);
   372	                    }
   373	                } catch (ClassNotFoundException e) {
   374	                    bugReporter.reportMissingClass(e);
   375	                }
   376	            }
   377	        } catch (CheckedAnalysisException e) {
   378	            AnalysisContext.logError("error:", e);
   379	        } finally {
   380	            profiler.end(CheckCallSitesAndReturnInstructions.class);
   381	        }
   382	    }
   383	
   384	    private void examineCallSite(Location location, ConstantPoolGen cpg, TypeDataflow typeDataflow)
