   237	            return Collections.emptyList();
   238	        }
   239	
   240	        List<ExecutableElement> builderMethods = ElementFilter.methodsIn( builderElement.getEnclosedElements() );
   241	        List<ExecutableElement> buildMethods = new ArrayList<>();
   242	        for ( ExecutableElement buildMethod : builderMethods ) {
   243	            if ( isBuildMethod( buildMethod, typeElement ) ) {
   244	                buildMethods.add( buildMethod );
   245	            }
   246	        }
