                         }
@@ -2896,7 +2896,7 @@ protected byte[] _decodeBase64(Base64Variant b64variant) throws IOException
                     if (ch == '"') {
                         decodedData >>= 4;
                         builder.append(decodedData);
-                        if (b64variant.usesPadding()) {
+                        if (b64variant.requiresPaddingOnRead()) {
