                         }
@@ -2936,7 +2936,7 @@ protected byte[] _decodeBase64(Base64Variant b64variant) throws IOException
                     if (ch == '"') {
                         decodedData >>= 2;
                         builder.appendTwoBytes(decodedData);
-                        if (b64variant.usesPadding()) {
